		err2 = QuantizeSingle(tls, c+2*32, mtx)
		*(*int16)(unsafe.Pointer(c + 3*32)) += int16((7*err1 + 8*err2) >> (4 - 1))
		err3 = QuantizeSingle(tls, c+3*32, mtx)
