		*(*int8)(unsafe.Pointer(left + 1)) = int8(3 * int32(*(*int8)(unsafe.Pointer(rd + 868 + uintptr(ch)*3 + 2))) >> 2)                                   //     ... 3/4th of err3
		*(*int8)(unsafe.Pointer(top)) = *(*int8)(unsafe.Pointer(rd + 868 + uintptr(ch)*3 + 1))                                                              //     ... err2
