	var lambda_d_i16 int32 = 106
	var lambda_d_i4 int32 = 11
	var lambda_d_uv int32 = 120
	var score_i4 int64 = (*VP8SegmentInfo)(unsafe.Pointer(dqm)).i4_penalty_
	var i4_bit_sum int64 = int64(0)
