					limit = (*(*uint32)(unsafe.Pointer(counts + uintptr(i)*4)) + *(*uint32)(unsafe.Pointer(counts + uintptr(i+1)*4)) + *(*uint32)(unsafe.Pointer(counts + uintptr(i+2)*4)) + *(*uint32)(unsafe.Pointer(counts + uintptr(i+3)*4)) + uint32(2)) / uint32(4)
