		var code_length int32 = int32(*(*uint8)(unsafe.Pointer((*HuffmanTreeCode)(unsafe.Pointer(tree)).code_lengths + uintptr(i))))

		*(*int32)(unsafe.Pointer(bp + uintptr(code_length)*4))++
